mapper.select:
SELECT comment_seq AS commentSeq, comment_board_seq AS commentBoardSeq,
comment_member_seq AS commentMemberSeq, comment_contents AS commentContents,
input_dt AS inputDt, update_dt AS updateDt
FROM party_board_comment
WHERE comment_board_seq = #{boardSeq}
limit #{startIdx}, #{size}

mapper.update:
UPDATE party_board_comment
SET comment_contents = #{commentContents},
update_dt = NOW()
WHERE
comment_seq = #{commentSeq}
AND comment_member_seq = #{commentMemberSeq}

mapper.delete:
DELETE FROM party_board_comment
WHERE
comment_seq = #{commentSeq}
AND comment_member_seq = #{commentMemberSeq}
