mapper.select:
SELECT
    member_seq AS memberSeq, member_user_seq AS memberUserSeq
    , member_role AS memberRole, member_gb AS memberGb
FROM party_member
WHERE member_party_seq = #{memberPartySeq}
AND member_user_seq = #{memberUserSeq}

mapper.update:
UPDATE party_member
SET member_role = #{memberRole}
WHERE member_party_seq = #{memberPartySeq}
AND  member_user_seq = #{memberUserSeq}

UPDATE party_member
SET member_gb = 0
, member_role = 0
WHERE member_party_seq = #{memberPartySeq}
AND  member_user_seq = #{memberUserSeq}
